package music;import java.util.ArrayList;import java.util.logging.Level;import java.util.logging.Logger;/** * The Functional Music project * @author Western State College, CIS412 class */public class MusTogether extends Music {    Music top;    Music bottom;    public MusTogether() {    }    public MusTogether(Music top) {        this.top = top;    }    public MusTogether(Music top, Music bottom) {        this.top = top;        this.bottom = bottom;    }    public BigRational perform(BigRational time, Modifier modifier, ArrayList<MusNote> notes) {        this.top.perform(time, modifier, notes);        return this.bottom.perform(time, modifier, notes);    }    public String toString() {        return "MusTogether (" + this.hashCode() + "): playing " + top.hashCode() + " ! " + bottom.hashCode();    }    public String prettyPrint() {        return "MusTogether(" + this.hashCode() + "): " + top.prettyPrint() + " ! " + bottom.prettyPrint();    }    public static Music musTogether(Music m1, Music m2) {        return new MusTogether(m1, m2);    }        public Music reverse(){        try {            BigRational td = this.top.duration();            BigRational bd = this.bottom.duration();            BigRational max = BigRational.max(td, bd);            if(max.equals(td))            {               MusNote er = Music.rest(td.minus(bd));               MusAfter bt = new MusAfter(er, this.bottom);               return new MusTogether(this.top,bt);            }            else            {               MusNote er = Music.rest(bd.minus(td));               MusAfter tt = new MusAfter(er, this.top);               return new MusTogether(this.bottom,tt);            }                    } catch (Exception ex) {            System.out.println(ex.getMessage());            return this;        }    }    @Override    public BigRational duration() {        try {            BigRational td = this.top.duration();            BigRational bd = this.bottom.duration();            BigRational max = BigRational.max(td, bd);            if (max.equals(td))return td;            else return bd;        } catch (Exception ex) {            System.out.println(ex.getMessage());            return null;        }    }    }