package music;import java.util.ArrayList;/** * The Functional Music project * @author Western State College, CIS412 class */public class MusTogether extends Music {    Music top;    Music bottom;    public MusTogether(){    }        public MusTogether(Music top){        this.top = top;    }      public MusTogether(Music top, Music bottom){        this.top = top;        this.bottom = bottom;    }    public Performance perform(double time, Modifier modifier){        Performance result = top.perform(time, modifier);        ArrayList<MusNote> notes = new ArrayList<MusNote>();        notes.addAll(bottom.perform(time+result.absoluteTime, modifier).notes);        for (MusNote musNote : notes) {            result.addAbs(musNote);        }        return result;    }    public String toString() {        return "MusTogether ("+ this.hashCode() + "): playing "                + top.hashCode() +" ! " +bottom.hashCode();    }    public String prettyPrint() {        return "MusTogether("+this.hashCode()+"): "+top.prettyPrint()+" ! "+bottom.prettyPrint();    }    public static Music musTogether(Music m1, Music m2){        return new MusTogether(m1, m2);    }}