package music;/** * The Functional Music project * @author Western State College, CIS412 class */public class ValFuncLambda extends ValFunc {    public Env env;    public Exp body;    public Symbol argName;    public ValFuncLambda(Env env, Exp body, Symbol argName) {        this.env = env;        this.body = body;        this.argName = argName;    }    @Override    public Value apply(Thunk t) {        Env newEnv = new EnvSingle(env, argName, t);        return body.eval(newEnv);         }}